mapper.update:
update board
   set status = 'N'
 where board_no = #{ boardNo }

update 
	   board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
 where board_no = #{boardNo}

mapper.insert:
	insert 
      into attachment
         (
           attachment_no
         , origin_name
         , change_name
         , file_path
         , ref_code
         , ref_no
         )
     values
        (
          seq_atno.nextval
        , #{originName}
        , #{changeName}
        , #{filePath}
        , 'B'
        , #{boardNo}
        )
