mapper.insert:
		insert
		  into mail_send
		     (
		       send_mail_no
          	 , emp_no
		  	 , sender_account
		  	 , mail_title
		  	 , mail_content
		  	 , send_date
		  	 , tem_status
          	 , status
          	 , wroteme_status
		  	 )
		values
		     (
		       seq_sdmno.nextval
		     , #{empName}
		  	 , #{senderAccount}
		  	 , #{mailTitle}
		  	 , #{mailContent}
		  	 , #{sendDate}
          	 , #{temStatus}
          	 , #{status}
          	 , #{wrotemeStatus}
		     )

	insert
      into mail_receive
         (
      	   receive_mail_no
      	 , send_mail_no
     	 , emp_no
      	 , receiver_account
      	 , read
      	 , important
      	 , spam
      	 , status
      	 )
    values
      	 (
      	   seq_rvmno.nextval
      	 , #{send_mail_no}
      	 , #{emp_no}
      	 , #{receiver_account}
      	 , #{read}
      	 , #{important}
      	 , #{spam}
      	 , #{status}
      	 )
