mapper.insert:
insert
  into employee
  (
    emp_no
  , emp_id
  , emp_pwd
  , emp_name
  , phone
  , email
  , address
  )
  values
  (
    seq_eno.nextval
  , #{empId}
  , #{empPwd}
  , #{empName}
  , #{phone}
  , #{email}
  , #{address}
  )

mapper.update:
update
       employee
   set emp_pwd = #{empPwd}
 where emp_id = #{empId}

update
       employee
   set emp_name = #{empName}
     , email = #{email}
     , phone = #{phone}
     , address = #{address}
     , profile = #{profile}
 where emp_id = #{empId}

update
       employee
   set emp_pwd = #{empPwd}
 where emp_no = #{empNo}

update
       employee
   set status = 'Y'
     , dept_code = #{deptCode}
     , team_code = #{teamCode}
     , job_code = #{jobCode}
 where emp_no = #{empNo}
