mapper.update:
update notice
   set status = 'N'
 where notice_no = #{ noticeNo }

update 
	   notice
   set notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
 where notice_no = #{noticeNo}

mapper.insert:
	insert 
      into attachment
         (
           attachment_no
         , origin_name
         , change_name
         , file_path
         , ref_code
         , ref_no
         )
     values
        (
          seq_atno.nextval
        , #{originName}
        , #{changeName}
        , #{filePath}
        , 'N'
        , #{noticeNo}
        )

mapper.update:
update
	   reply
   set reply_content=#{replyContent}
 where reply_no = #{replyNo}
